const hrefRegex = /href\s*=\s*["']([^"']+)["']/gi;
const excludePatterns = ['php','css','png','jpg','fonts','feed','comments','/categoria/','cdn','googletagmanager','zapwp','wp','whatsapp','facebook','twitter','youtube','tiktok','instagram'];

for (const item of $input.all()) {
